PREFIX : <http://amrita.sony.org/terms#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT ?polyhouse WHERE { ?polyhouse rdf:type :Polyhouse . }

PREFIX : <http://amrita.sony.org/terms#>
PREFIX bot: <https://w3id.org/bot#>

SELECT ?grid WHERE {
  :Polyhouse_P1 bot:hasStorey ?storey .
  ?storey bot:hasSpace ?grid .
}

PREFIX : <http://amrita.sony.org/terms#>
PREFIX bot: <https://w3id.org/bot#>

SELECT (COUNT(?element) AS ?count)
WHERE {
  :Polyhouse_P1 bot:containsElement ?element .
}

PREFIX : <http://amrita.sony.org/terms#>
PREFIX bot: <https://w3id.org/bot#>
PREFIX sosa: <http://www.w3.org/ns/sosa/>

SELECT ?connectedTo WHERE {
  { :Space_1_P1 bot:containsElement ?connectedTo }
  UNION
  { ?connectedTo bot:containsElement :Space_1_P1 }
  UNION
  { :Space_1_P1 sosa:hosts ?connectedTo }
  UNION
  { ?connectedTo sosa:hosts :Space_1_P1 }
  UNION
  { :Space_1_P1 bot:adjacentElement ?connectedTo }
  UNION
  { ?connectedTo bot:adjacentElement :Space_1_P1 }
}

PREFIX : <http://amrita.sony.org/terms#>
PREFIX sosa: <http://www.w3.org/ns/sosa/>

SELECT ?sensor ?observation WHERE {
  ?sensor sosa:madeObservation ?observation .
}

PREFIX : <http://amrita.sony.org/terms#>
PREFIX bot: <https://w3id.org/bot#>
PREFIX mep: <https://pi.pauwel.be/voc/distributionelement#>
PREFIX sosa: <http://www.w3.org/ns/sosa/>

SELECT ?camera WHERE {
  ?camera a mep:AudioVisualAppliance-CAMERA .
  ?polyhouse bot:containsElement ?camera .
  ?polyhouse a :Polyhouse .
  FILTER NOT EXISTS { ?camera sosa:madeObservation ?observation . }
}

PREFIX amrita: <http://amrita.sony.org/terms#>
PREFIX mls: <http://www.w3.org/ns/mls#>

SELECT ?task WHERE {
  ?task a mls:Task .
  amrita:DNN_ES mls:defines ?task .
}

PREFIX amrita: <http://amrita.sony.org/terms#>
PREFIX mls: <http://www.w3.org/ns/mls#>
PREFIX dcterms: <http://purl.org/dc/terms/>
SELECT ?hyperparameter ?value ?activation WHERE {
  ?hyperparameter a mls:HyperParameter .
  ?hyperparameter mls:hasValue ?value .
  OPTIONAL { ?hyperparameter amrita:hasActivation ?activation }
}

PREFIX : <http://amrita.sony.org/terms#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
SELECT ?polyhouseName WHERE {
?polyhouse a :Polyhouse ;
rdfs:label ?polyhouseName .
}
